import React from "react"
import {setSearch} from "../application/actions/Search";
import {setCurrNode} from "../application/actions/Graph";
import {connect} from "react-redux";

function View(props) {
  return (
    <div style={{backgroundColor: "#f1f1f1", height: "100%", flex: 1}}>
      <div style={{display: "flex", backgroundColor: "red", flex: 1}}>

        <div style={{flex: 1, display: "flex", alignItems: "center", justifyContent: "center"}}>
          <div>{props.currNode == null ? "null" : props.currNode.goal}</div>
        </div>

        <div style={{flex: 1, display: "flex", alignItems: "center", justifyContent: "center"}}>
          <div>{
            props.currNode !== null && typeof(props.currNode) !== "string" &&
            <div>{props.currNode.steps.map((step) => (
              <div>{step.goal}</div>
            ))}</div>
          }</div>
        </div>
      </div>

    </div>
  )
}

/* Redux */
const mapStateToProps = (state) => {
  return {
    search: state.searchReducer.search,
    currNode: state.graphReducer.currNode
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    callSearch: (search) => dispatch(setSearch(search)),
    setCurrNode: (search) => dispatch(setCurrNode(search))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(View)
