import {SETSEARCH} from "../Types";

const initialState = {
  search: "bruh",
}

const searchReducer = (state = initialState, action) => {
  const format = (s) => {
    if (typeof s !== 'string') return ''
    s =  s.toLowerCase()
    if (s.charAt(s.length) === ".") {
      return s.charAt(0).toUpperCase() + s.slice(1)
    } else {
      return s.charAt(0).toUpperCase() + s.slice(1) + "."
    }
  }

  switch (action.type) {

    case SETSEARCH:
      console.log(format(action.data) + "bruh")
      return {
        ...state,
        search: format(action.data)
      }
    default:
      return state;
  }
}

export default searchReducer
