import {SETCURRNODE, SETSEARCH} from "../Types";

const initialState = {
  currNode: null
}

const graphReducer = (state = initialState, action) => {
  switch (action.type) {
    case SETCURRNODE:
      return {
        ...state,
        currNode: action.data
      }
    default:
      return state;
  }
}

export default graphReducer
