import {SETCURRNODE, SETSEARCH} from "../Types";
import {TestData} from "../TestData";

const initialState = {
  currNode: null,
  graphData: TestData
}

const graphReducer = (state = initialState, action) => {
  function bfs() {
    let queue = [];

    for (let i = 0; i < state.graphData.length; i++) {
      queue.add(state.graphData[i]);
    }
  }

  switch (action.type) {
    case SETCURRNODE:
      return {
        ...state,
        currNode: action.data
      }
    default:
      return state;
  }
}

export default graphReducer
