import React from "react"
import {setSearch} from "../application/actions/Search";
import {selectChild, setCurrNode} from "../application/actions/Graph";
import {connect} from "react-redux";
import AddIcon from '@material-ui/icons/Add';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import GridLines from 'react-gridlines';

function View(props) {
  return (
    <div style={{backgroundColor: "#222222", height: "100%", flex: 1}}>


      <div style={{display: "flex", flex: 1, height: "100%"}}>



        <div style={{flex: 1, display: "flex", alignItems: "center", justifyContent: "center"}}>
          <div className={"backButton"}>
            <ChevronLeftIcon style={{color: "white"}}/>
          </div>

          {props.currNode !== null && typeof props.currNode !== "string" &&
            <div
              style={{backgroundColor: "#2b56d2", padding: 15, borderRadius: 50, color: "white"}}
            >{props.currNode == null ? "null" : props.currNode.goal}</div>
          }
        </div>

        <div style={{flex: 1, display: "flex", alignItems: "center", justifyContent: "center"}}>
          <div>{
            props.currNode !== null && typeof(props.currNode) !== "string" &&
            <div>{props.currNode.steps.map((step, index) => (
              <div style={{display: "flex", alignItems: "center"}}>
                <div
                  style={{backgroundColor: "#2b56d2", padding: 15, borderRadius: 50, color: "white", margin: 10}}
                >{step.goal}</div>

                <div
                  className={"plusButton"}
                >
                  <AddIcon style={{color: "white"}}/>
                </div>
              </div>
            ))}</div>
          }</div>
        </div>
      </div>

    </div>
  )
}

/* Redux */
const mapStateToProps = (state) => {
  return {
    search: state.searchReducer.search,
    currNode: state.graphReducer.currNode
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    callSearch: (search) => dispatch(setSearch(search)),
    setCurrNode: (search) => dispatch(setCurrNode(search)),
    selectChild: (i) => dispatch(selectChild(i))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(View)
