import {GOBACK, SELECTCHILD, SETCURRNODE, SETSEARCH} from "../Types";
import {TestData} from "../TestData";

const initialState = {
  currNode: null,
  prevNodeStack: [],
  graphData: TestData
}

const graphReducer = (state = initialState, action) => {
  function bfs(search) {
    let queue = [];
    let curr = state.graphData[0]

    for (let i = 0; i < state.graphData.length; i++) {
      queue.push(state.graphData[i]);
    }

    while (queue.length > 0) {
      let v = queue.shift()
      if (v.goal === search) {
        return curr;
      } else {
        for (let i = 0; i < curr.steps.length; i++) {
          queue.push(curr.steps[i])
        }
      }
    }

    return null;
  }

  switch (action.type) {
    case GOBACK:
      return {
        ...state,
        currNode: state.prevNodeStack[state.prevNodeStack.length - 1],
        prevNodeStack: state.prevNodeStack.slice(0, state.prevNodeStack.length - 1)
      }
    case SELECTCHILD:
      return {
        ...state,
        prevNodeStack: state.prevNodeStack.concat(state.currNode),
        currNode: state.currNode.steps[action.data]
      }
    case SETCURRNODE:
      console.log(bfs(action.data))
      return {
        ...state,
        prevNode: null,
        currNode: state.graphData[0]
      }
    default:
      return state;
  }
}

export default graphReducer
