import {SELECTCHILD, SETCURRNODE, SETSEARCH} from "../Types";
import {TestData} from "../TestData";

const initialState = {
  currNode: null,
  prevNode: null,
  graphData: TestData
}

const graphReducer = (state = initialState, action) => {
  function bfs() {
    let queue = [];

    for (let i = 0; i < state.graphData.length; i++) {
      queue.push(state.graphData[i]);
    }


  }

  switch (action.type) {
    case SELECTCHILD:
      return {
        ...state,
        currNode: state.currNode.steps[action.data]
      }
    case SETCURRNODE:
      return {
        ...state,
        prevNode: null,
        currNode: state.graphData[0]
      }
    default:
      return state;
  }
}

export default graphReducer
