import React, {useState} from 'react'
import SearchIcon from '@material-ui/icons/Search';
import {connect} from "react-redux";
import "./Styles.css"
import ButtonGroup from "@material-ui/core/ButtonGroup";
import Button from "@material-ui/core/Button";
import {setSearch} from "../application/actions/Search";

function Search(props) {
  const [search, setSearch] = useState("");

  return (
  <div>
    <div style={{display: "flex", justifyContent: "center", marginBottom: 10}}>
      <ButtonGroup variant="text" color="primary" aria-label="text primary button group">
        <Button style={{width: 150}}>Goal-Step</Button>
        <Button style={{width: 150}}>Graph</Button>
      </ButtonGroup>
    </div>
    <div style={{display: "flex", alignItems: "center", justifyContent: "center"}}>
      <div className={"searchDiv"}>
        <input type="text" name="name" className={"searchInput"} onChange={(e) => setSearch(e.target.value)}/>
      </div>

      <div className={"searchButton"} onClick={() => props.setSearch(search)}>
        <SearchIcon style={{color: "white"}}/>
      </div>
    </div>

    <div>{search}</div>
    <div>{props.search}</div>

  </div>
  )
}

/* Redux */
const mapStateToProps = (state) => {
  return {
    search: state.searchReducer.search
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    setSearch: () => dispatch(setSearch())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Search)
